var TableDatatablesManaged = function () {
    
    var a = function () {
        var e = $("#sample_3");
        e.dataTable({
            language: {aria: {},
                emptyTable: "Nessun risultato trovato",
                info: "Mostra _START_ di _END_ su _TOTAL_ risultati",
                infoEmpty: "Nessun risultato trovato",
                infoFiltered: "(filtrati su _MAX_ totali)",
                lengthMenu: "Mostra _MENU_", search: "Cerca",
                zeroRecords: "Nessun risultato trovato",
                paginate: {previous: "Precedente", next: "Successiva", last: "Ultima", first: "Prima"}},
            bStateSave: !0,
            lengthMenu: [
                [25, 50, 100, -1],
                [25, 50, 100, "All"]
            ],
            pageLength: 25,
            columnDefs: [
                {orderable: !1, targets: [0]},
                {searchable: !1, targets: [0]}
            ],
            buttons: [
                {extend: "excel", className: "btn green-jungle btn-outline", text: "<i class='fa fa-file-excel-o tooltips'></i> Excel"},
                {extend: "colvis", className: "btn dark btn-outline", text: "Colonne"}]
            ,
            colReorder: {reorderCallback: function () {
                    console.log("callback");
                }},
            order: [],
            dom: "<'row' <'col-md-12'B>><'row'<'col-md-6 col-sm-12'l><'col-md-6 col-sm-12'f>r><'table-scrollable't><'row'<'col-md-5 col-sm-12'i><'col-md-7 col-sm-12'p>>",
            sAjaxSource: "/Query?type=2"
        });
//        jQuery("#sample_3_wrapper");

        $("tfoot input").keyup(function () {
            e.fnFilter(this.value, e.oApi._fnVisibleToColumnIndex(
                    e.fnSettings(), $("tfoot input").index(this)));
        });
        $("tfoot input").each(function (i) {
            this.initVal = this.value;
        });
        $("tfoot input").focus(function () {
            if (this.className === "form-control")
            {
                this.className = "form-control";
                this.value = "";
            }
        });
        $("tfoot input").blur(function (i) {
            if (this.value === "")
            {
                this.className = "form-control";
                this.value = this.initVal;
            }
        });

    };
    return{init: function () {
            jQuery().dataTable && (a());
        }};
}();
App.isAngularJsApp() === !1 && jQuery(document).ready(function () {
    TableDatatablesManaged.init();
});